const ph=s=>{if(!s)return[];return s.split(",").map(e=>{const[y,p]=e.split(":");return{y:parseInt(y),p};});};
const pn=p=>{if(!p||p==="MC"||p==="WD"||p==="DNP"||p==="CUT"||p==="DQ")return 75;const n=parseInt(p.replace(/[^0-9]/g,""));return isNaN(n)?75:n;};
const ps=positions=>{if(!positions.length)return 20;const sc=positions.map(p=>{const n=pn(p);return n<=1?100:n<=2?92:n<=3?87:n<=5?78:n<=8?67:n<=10?60:n<=15?48:n<=20?38:n<=25?28:n<=30?20:Math.max(3,15-(n-30)*0.3);});const wt=[0.30,0.25,0.20,0.15,0.10];const wS=sc.slice(0,5).reduce((a,s,i)=>a+s*(wt[i]||0.10),0);const wD=wt.slice(0,Math.min(5,sc.length)).reduce((a,b)=>a+b,0);return Math.round(wS/wD);};
const calcForm=pl=>ps(pl.form.map(f=>f.p));
const calcHist=(pl,t)=>{const h=ph(pl.hist[t.id]);return h.length?ps(h.slice(0,5).map(e=>e.p)):20;};
const calcFit=(pl,t)=>{let s=50;const ks=t.ks||[];if(ks.includes("drvDist"))s+=(pl.dd-300)*0.35;if(ks.includes("drvAcc"))s+=(pl.da-62)*2.0;if(ks.includes("sgApp"))s+=pl.sga*12;if(ks.includes("sgT2G"))s+=pl.sg2*6;if(ks.includes("sgPutt"))s+=pl.sgp*14;if(ks.includes("sgTot"))s+=pl.sgt*4;if(ks.includes("scr"))s+=(pl.scr-61)*1.5;const ch=t.ch.map(c=>c.toLowerCase());if(ch.includes("long")&&pl.dd>=310)s+=5;if((ch.includes("tight")||ch.includes("narrow"))&&pl.da>=65)s+=5;if(ch.includes("small greens")&&pl.sga>=0.8)s+=6;if((ch.includes("wind")||ch.includes("links"))&&pl.str.some(x=>x.toLowerCase().includes("wind")||x.toLowerCase().includes("links")))s+=10;return Math.max(0,Math.min(100,Math.round(s)));};
const calcRk=rk=>Math.max(0,Math.round(100-rk*2.2));
const predict=(pl,t)=>{const f=calcForm(pl),h=calcHist(pl,t),c=calcFit(pl,t),r=calcRk(pl.rk);return{total:Math.round((f*0.40+h*0.40+c*0.15+r*0.05)*10)/10,form:f,hist:h,fit:c,owgr:r};};
